<script type="text/javascript">
 function deleteItem(obj){
	item = $(obj).parent().parent();
	category_id = item.find('#category_id').val();
	if( category_id <= 0)
		item.remove();
	if( category_id > 0 && confirm("确认要删除这个分类吗？此操作不可恢复，删除后该分类下的预约会自动转移到默认分类下！") ){
		$.post("<?php echo $this -> createWebUrl('Category', array('gweid' => $_W['gweid'] , 'action' => 'delete')); ?>", { id: category_id },
			function (data, textStatus){
				// data 可以是 xmlDoc, jsonObj, html, text, 等等.
				//this; // 这个Ajax请求的选项配置信息，请参考jQuery.get()说到的this
				if ( data.code == 'success' ){
					item.remove();
					$("#category_default > tr").find(":input[name='category_id[]'][value='" + category_id + "']");
				}
					
			}, "json");
	}
}
 function addItem(){
	item =  '<tr>'+
			'	<td><input name="category_id[]" id="category_id" type="hidden" value="-1"/><input name="name[]" type="text" class="span2" value="" placeholder="预约名称"/></td>'+
			'	<td><input type="text" class="span2" value="保存后可见" /></td>'+
			'	<td><input type="text" class="span2" value="保存后可见" /></td>'+
			'	<td><a href="javascript:;" onclick="deleteItem(this)" class="icon-remove-sign" style="margin-top:10px;" title="删除此分类"></a></td>'+
			'</tr>';
	$('#re-items').append(item);
 }
function submit(){
	$("#category_form").ajaxSubmit({
		success: function(data){
			json_parse_to_category(data);
			$("#re-items > tr[id!='category_all']").remove();
			$('#re-items').append($('#category_default tr').clone());
			$('#status_alert').css('display','block');
			setTimeout(function(){
				$('#status_alert').css('display','none');
			},2500);
		},
		error: function(data){
			json_parse_to_category(data);
			$("#re-items > tr[id!='category_all']").remove();
			$('#re-items').append($('#category_default tr').clone());
			$('#status_alert').css('display','block');
			setTimeout(function(){
				$('#status_alert').css('display','none');
			},2500);
			
		},
		dataType: 'json'
	});
}
function json_parse_to_category(obj){
	console.info(obj);
	$('#category_default').empty();
	$.each(obj,function(){
		console.info(this);
		item =  '<tr>'+
			'	<td><input name="category_id[]" id="category_id" type="hidden" value="'+this.id+'"/><input name="name[]" type="text" class="span2" value="'+this.name+'" placeholder="预约名称"/></td>'+
			'	<td><input type="text" class="span2" value="'+this.activeUrl+'" /></td>'+
			'	<td><input type="text" class="span2" value="'+this.endedUrl+'" /></td>'+
			'	<td><a href="javascript:;" onclick="deleteItem(this)" class="icon-remove-sign" style="margin-top:10px;" title="删除此分类"></a></td>'+
			'</tr>';
		$('#category_default').append(item);
	});	
}

$('#category').on('show', function () {
	$("#category_default").empty();
	$('#category_default').append($("#re-items > tr[id!='category_all']").clone());
})
$('#category').on('hidden', function () {
	$("#re-items > tr[id!='category_all']").remove();
	$('#re-items').append($('#category_default tr').clone());
  // do something…
})

